'                       p_add_comma => true )       ||',
'                   apex_javascript.add_attribute (',
'                       p_name      => ''primaryColor'',',
'                       p_value     => l_primary_color,',
'                       p_add_comma => true )       ||',
'                   apex_javascript.add_attribute (',
'                       p_name      => ''textColor'',',
'                       p_value     => l_text_color,',
