'                   apex_javascript.add_attribute (',
'                       p_name      => ''backgroundColor'',',
'                       p_value     => l_background_color,',
'                       p_add_comma => true )       ||',
'                   apex_javascript.add_attribute (',
'                       p_name      => ''borderColor'',',
'                       p_value     => l_border_color,',
'                       p_add_comma => false )      || ''},'' ||',
'                   apex_javascript.add_value (',
'                       p_value     => l_logging,',
'                       p_add_comma => false ) || '');'' );',
'',
'    p_result.is_navigable := true;',
'  end if;',
'exception',
'  when others then',
'    raise;',
'end render_timedropper;'))
